INSERT INTO `services` (`id`, `title`, `description`, `detail`, `image_url`, `createdAt`, `updatedAt`, `appliesTo`, `slug`, `price`) VALUES
	(1, 'Khám sức khỏe tổng quát', 'Dịch vụ kiểm tra sức khỏe định kỳ toàn diện giúp phát hiện sớm các yếu tố nguy cơ bệnh lý tim mạch, tiểu đường, rối loạn mỡ máu, gan, thận… và đưa ra phác đồ chăm sóc phù hợp.', 'Bệnh nhân đăng ký khám sẽ được kiểm tra chiều cao, cân nặng, huyết áp, chỉ số BMI. Tiếp đó là xét nghiệm máu tổng quát, chức năng gan – thận, đường huyết, mỡ máu, nước tiểu.\nSau đó, bệnh nhân được bác sĩ nội tổng quát tư vấn kết quả và đưa ra khuyến nghị cụ thể về chế độ ăn uống, tập luyện và lịch tái khám.\nMột số trường hợp có thể được chỉ định thêm siêu âm tổng quát, đo điện tim, chụp X-quang ngực tùy theo nhu cầu', '/uploads/1749914163336-806018910.svg', '2025-06-07 16:53:40', '2025-06-26 08:11:16', NULL, NULL, 500000),
	(2, 'Tư vấn điều trị tiểu đường', 'Theo dõi, kiểm soát đường huyết hiệu quả.', 'Chẩn đoán phân loại, kê thuốc hạ đường, tư vấn dinh dưỡng.', '/uploads/1749915011327-269060672.svg', '2025-06-07 16:53:43', '2025-06-22 15:48:53', '["f_u60", "fm_u60", "m_u60"]', NULL, 500000),
	(3, 'Điện tâm đồ (ECG) nghỉ', 'Điện tâm đồ là kỹ thuật không xâm lấn giúp ghi lại hoạt động điện của tim, hỗ trợ chẩn đoán các rối loạn nhịp tim, thiếu máu cơ tim, dày thất, rối loạn dẫn truyền… một cách nhanh chóng và chính xác.', 'Bệnh nhân nằm nghỉ, không nói chuyện, tháo bỏ vật kim loại và các thiết bị ảnh hưởng đến sóng điện tim.\nKỹ thuật viên gắn 10 điện cực lên vị trí tiêu chuẩn ở tay, chân và ngực.\nMáy ECG sẽ ghi lại biểu đồ hoạt động tim trong khoảng 1–2 phút.\nKết quả được bác sĩ chuyên khoa tim mạch đọc ngay tại chỗ để phát hiện bất thường và đưa ra hướng xử trí nếu cần.', '/uploads/1749915072583-985837555.svg', '2025-06-07 16:53:46', '2025-06-22 16:26:31', NULL, NULL, 500000),
	(4, 'Khám và tư vấn tăng huyết áp', 'Dịch vụ giúp đánh giá tình trạng tăng huyết áp ở người bệnh, phát hiện sớm các biến chứng liên quan và đưa ra phác đồ điều trị cá nhân hóa nhằm kiểm soát huyết áp hiệu quả và bền vững.', 'Bệnh nhân được đo huyết áp bằng máy điện tử nhiều lần tại chỗ và ghi nhận bằng nhật ký huyết áp tại nhà nếu cần.\nThăm khám lâm sàng hệ tim mạch – mạch ngoại vi, đánh giá các yếu tố nguy cơ như béo phì, tiểu đường, hút thuốc…\nThực hiện các xét nghiệm như mỡ máu, đường huyết, chức năng thận và điện tâm đồ.\nBác sĩ tim mạch sẽ phân tầng nguy cơ, kê toa thuốc phù hợp và tư vấn thay đổi lối sống. Trường hợp cần theo dõi sát sẽ được đặt hẹn định kỳ hoặc giới thiệu đo huyết áp 24 giờ.', '/uploads/1749915084957-552674204.svg', '2025-06-07 16:53:49', '2025-06-22 16:26:03', '["fm_u60", "fm_60"]', NULL, 500000),
	(5, 'Siêu âm tim Doppler màu', 'Siêu âm tim là phương pháp chẩn đoán hình ảnh giúp đánh giá cấu trúc và chức năng tim, phát hiện các bệnh lý như hở van tim, dày thành tim, tràn dịch màng tim, bệnh tim bẩm sinh...', 'Bệnh nhân nằm nghiêng trái, ngực được bôi gel siêu âm để tăng độ truyền sóng.\nBác sĩ sử dụng đầu dò siêu âm để khảo sát tim qua nhiều mặt cắt. Kỹ thuật Doppler màu và phổ giúp đánh giá lưu lượng máu qua các buồng tim và van tim.\nToàn bộ hình ảnh và chỉ số được ghi lại và phân tích trực tiếp. Sau khi hoàn tất, bác sĩ sẽ đọc kết quả và tư vấn tình trạng tim mạch cụ thể cho bệnh nhân.', '/uploads/1749915099714-29109445.svg', '2025-06-07 16:53:53', '2025-06-22 16:26:50', '["f_60", "m_60"]', NULL, 500000),
	(6, 'Tư vấn phòng ngừa đột quỵ', 'Đánh giá nguy cơ và hướng dẫn thay đổi lối sống.', 'Kiểm tra huyết áp, đường huyết, mỡ máu, ECG, lối sống và thuốc phòng ngừa.dsds', '/uploads/1749915125019-439319405.svg', '2025-06-07 16:53:56', '2025-06-22 15:49:51', NULL, NULL, 500000),
	(7, 'Khám sức khỏe tổng quát cho trẻ em', 'Dịch vụ giúp đánh giá toàn diện thể chất – dinh dưỡng – phát triển của trẻ từ sơ sinh đến 16 tuổi, tầm soát sớm các rối loạn tăng trưởng, bệnh lý mãn tính và hướng dẫn chăm sóc phù hợp theo từng độ tuổi.', 'Bác sĩ Nhi tiếp nhận thông tin sức khỏe, tiền sử sinh – bệnh, lịch tiêm chủng và chiều cao – cân nặng hiện tại.\nĐánh giá phát triển thể chất (BMI, chiều cao, vòng đầu), tinh thần vận động – ngôn ngữ theo độ tuổi.\nThực hiện các xét nghiệm cơ bản: công thức máu, đường huyết, phân tích nước tiểu, vi chất (nếu cần).\nTư vấn dinh dưỡng, chế độ vận động và lịch hẹn theo dõi định kỳ cho trẻ.', '/uploads/1749915138959-529655092.svg', '2025-06-07 16:54:00', '2025-06-22 16:29:07', NULL, NULL, 500000),
	(8, 'Điều trị viêm hô hấp cấp', 'Chăm sóc trẻ bị ho, sốt, sổ mũi.', 'Chẩn đoán nhiễm siêu vi, kê thuốc phù hợp, hướng dẫn chăm sóc tại nhà.', '/uploads/1749915162167-306789961.svg', '2025-06-07 16:54:03', '2025-06-22 15:50:13', NULL, NULL, 500000),
	(9, 'Tư vấn tiêm chủnggg', 'Lập kế hoạch và theo dõi tiêm chủng cho trẻ.', 'Tư vấn lịch tiêm chủng mở rộng, vaccine dịch vụ, tư vấn phản ứng sau tiêm.', '/uploads/1749915180552-593991317.svg', '2025-06-07 16:54:06', '2025-06-22 15:50:20', NULL, NULL, 500000),
	(10, 'Nội soi tai – mũi – họng', 'Dịch vụ giúp khảo sát hình ảnh chi tiết cấu trúc tai, mũi, họng – hỗ trợ phát hiện polyp mũi, viêm xoang, viêm thanh quản, u vòm họng... với độ chính xác cao.', 'Bệnh nhân được gây tê niêm mạc mũi – họng bằng thuốc xịt chuyên dụng.\nBác sĩ sử dụng ống nội soi mềm hoặc ống cứng đường kính nhỏ để quan sát từng vị trí: hốc mũi, vòm mũi họng, thanh quản và tai giữa qua vòi Eustache.\nToàn bộ hình ảnh được hiển thị trên màn hình và lưu trữ nếu cần theo dõi tiến triển bệnh.\nSau nội soi, bác sĩ tư vấn kết luận và hướng điều trị phù hợp.', '/uploads/1749915188997-551736278.svg', '2025-06-07 16:54:09', '2025-06-22 16:33:42', NULL, NULL, 500000),
	(11, 'Điều trị viêm xoang', 'Điều trị viêm xoang cấp và mãn tính không xâm lấn.', 'Thuốc kháng sinh, xông mũi, hút dịch xoang, hỗ trợ bằng vật lý trị liệu.', '/uploads/1749915198239-662343352.svg', '2025-06-07 16:54:13', '2025-06-14 15:33:18', NULL, NULL, 500000),
	(12, 'Phẫu thuật cắt amidan', 'Giảm tái phát viêm amidan gây sốt và đau họng mạn tính.', 'Phẫu thuật không đau bằng dao plasma hoặc laser hiện đại.', '/uploads/1749915213535-839922542.svg', '2025-06-07 16:54:15', '2025-06-14 15:33:33', NULL, NULL, 500000),
	(13, 'Khám và điều trị mụn trứng cá', 'Dịch vụ chuyên sâu chẩn đoán và điều trị mụn trứng cá ở mọi cấp độ – từ mụn ẩn, mụn viêm đến mụn bọc nặng, giúp kiểm soát nhờn, giảm thâm sẹo và phục hồi làn da khỏe mạnh.', 'Bác sĩ da liễu thăm khám trực tiếp vùng da bị mụn, phân loại mức độ (nhẹ – trung bình – nặng).\nKhai thác thói quen chăm sóc da, chế độ ăn uống, nội tiết và yếu tố stress.\nChỉ định điều trị bằng thuốc bôi, thuốc uống (retinoid, kháng sinh, nội tiết...), kết hợp liệu trình hỗ trợ như lấy nhân mụn y khoa, ánh sáng xanh, peel da hóa học.\nHướng dẫn chăm sóc da tại nhà và theo dõi định kỳ để tránh tái phát.', '/uploads/1749915229472-59528165.svg', '2025-06-07 16:54:19', '2025-06-22 16:34:59', NULL, NULL, 500000),
	(14, 'Laser trị nám, tàn nhang', 'Xoá mờ vết nám, tàn nhang với công nghệ hiện đại.', 'Ứng dụng laser YAG, Fractional CO2, IPL tuỳ tình trạng da.', '/uploads/1749915249455-745683908.svg', '2025-06-07 16:54:21', '2025-06-22 15:51:00', NULL, NULL, 500000),
	(15, 'Khám và điều trị nấm da', 'Xử lý viêm da, lang ben, hắc lào, nấm móng.', 'Dùng thuốc kháng nấm đường bôi hoặc uống tuỳ vùng tổn thương.', '/uploads/1749915264471-398751210.svg', '2025-06-07 16:54:25', '2025-06-16 20:28:17', NULL, NULL, 500000),
	(16, 'Đo đường huyết lúc đóiiii', ' Xét nghiệm máu để xác định mức glucose trong máu sau khi nhịn ăn ít nhất 8 giờ. Giúp chẩn đoán sớm tiểu đường hoặc tiền tiểu đường', NULL, '/uploads/1750141207367-252009574.svg', '2025-06-14 18:20:29', '2025-06-22 15:51:13', '[]', NULL, 500000),
	(18, 'Khám mắt ', 'Tầm soát sức khỏe toàn diện, phát hiện bệnh sớm.', NULL, '/uploads/1750141224503-431960217.jpg', '2025-06-17 06:20:24', '2025-06-17 06:20:24', '[]', 'kham-mat', 500000),
	(20, 'Nội soi dạ dày gây mê', 'Dịch vụ nội soi dạ dày gây mê giúp kiểm tra trực tiếp niêm mạc thực quản – dạ dày – tá tràng mà không gây cảm giác khó chịu cho bệnh nhân, phù hợp cho cả người lớn tuổi và trẻ em.', 'Bệnh nhân sẽ được bác sĩ chuyên khoa thăm khám tổng quát và khai thác các triệu chứng liên quan như ợ nóng, đau bụng, khó tiêu, trào ngược... Sau đó, thực hiện các xét nghiệm cần thiết để đánh giá trước khi gây mê như công thức máu, chức năng gan thận.\nSau khi được bác sĩ gây mê đánh giá, bệnh nhân được đưa vào phòng nội soi, thực hiện gây mê ngắn qua đường tĩnh mạch.\nQuá trình nội soi diễn ra trong khoảng 5–10 phút với ống nội soi mềm. Bệnh nhân sau đó được theo dõi tại phòng hồi tỉnh.\nSau khi tỉnh táo, bác sĩ sẽ đọc kết quả và tư vấn phương án điều trị phù hợp nếu phát hiện bất thường.', '/uploads/1750608992115-490030313.svg', '2025-06-22 16:16:32', '2025-06-22 16:16:32', '[]', 'noi-soi-da-day-gay-me', 500000),
	(21, 'Tầm soát ung thư tiêu hóa định kỳ', 'Gói tầm soát chuyên sâu nhằm phát hiện sớm các dấu hiệu ung thư dạ dày, đại tràng – hai loại ung thư tiêu hóa phổ biến và nguy hiểm. Việc kiểm tra định kỳ giúp tăng khả năng điều trị hiệu quả nếu phát hiện bệnh ở giai đoạn sớm.', 'Bệnh nhân sẽ được bác sĩ tư vấn chuyên môn để đánh giá yếu tố nguy cơ (di truyền, ăn uống, tuổi tác...). Các xét nghiệm máu bao gồm chất chỉ điểm ung thư (CEA, CA19-9), H.pylori, men gan, chức năng thận sẽ được thực hiện.\nSau đó bệnh nhân được thực hiện nội soi dạ dày có gây mê để kiểm tra tổn thương niêm mạc, polyp hoặc loét.\nVới bệnh nhân trên 40 tuổi hoặc có nguy cơ cao, nội soi đại tràng cũng được chỉ định.\nCuối cùng, bác sĩ sẽ tổng hợp kết quả, trả lời trực tiếp và hướng dẫn phương án theo dõi hoặc điều trị nếu cần.', '', '2025-06-22 16:17:17', '2025-06-22 16:17:17', '[]', 'tam-soat-ung-thu-tieu-hoa-inh-ky', 500000),
	(22, 'Điều trị trào ngược dạ dày thực quản', 'Dịch vụ điều trị trào ngược giúp kiểm soát triệu chứng và ngăn ngừa biến chứng ở những bệnh nhân thường xuyên ợ nóng, buồn nôn, cảm giác nghẹn hoặc đau tức ngực không do tim mạch.', 'Bác sĩ chuyên khoa sẽ trao đổi chi tiết với bệnh nhân về tiền sử bệnh lý, thói quen ăn uống và triệu chứng gặp phải. Sau đó, tiến hành thăm khám lâm sàng vùng thượng vị và thực hiện các xét nghiệm liên quan như test HP, nội soi dạ dày có sinh thiết nếu cần.\nDựa trên kết quả, bác sĩ kê toa điều trị gồm thuốc ức chế acid, bảo vệ niêm mạc và điều chỉnh chế độ ăn uống, sinh hoạt. Trường hợp nặng có thể được tư vấn phẫu thuật hoặc can thiệp nội soi nâng cao.', '', '2025-06-22 16:18:24', '2025-06-22 16:18:24', '[]', 'ieu-tri-trao-nguoc-da-day-thuc-quan', 500000),
	(23, 'Xét nghiệm tìm vi khuẩn HP', 'Kiểm tra sự hiện diện của vi khuẩn Helicobacter pylori – nguyên nhân chính gây viêm loét dạ dày, ung thư dạ dày. Xét nghiệm nhanh, chính xác giúp xác định sớm để điều trị kịp thời.', 'Bệnh nhân được tư vấn lựa chọn phương pháp xét nghiệm phù hợp: test hơi thở (urease breath test), xét nghiệm phân, xét nghiệm máu hoặc nội soi sinh thiết.\nTrường hợp test hơi thở, bệnh nhân cần nhịn ăn ít nhất 6 tiếng trước khi test. Quy trình test diễn ra nhanh chóng, an toàn, không xâm lấn.\nKết quả sẽ được bác sĩ giải thích và tư vấn điều trị diệt HP nếu dương tính.', '', '2025-06-22 16:18:50', '2025-06-22 16:18:50', '[]', 'xet-nghiem-tim-vi-khuan-hp', 500000),
	(24, 'Điều trị viêm loét dạ dày tá tràng', 'Kiểm tra sự hiện diện của vi khuẩn Helicobacter pylori – nguyên nhân chính gây viêm loét dạ dày, ung thư dạ dày. Xét nghiệm nhanh, chính xác giúp xác định sớm để điều trị kịp thời.', 'Bệnh nhân được tư vấn lựa chọn phương pháp xét nghiệm phù hợp: test hơi thở (urease breath test), xét nghiệm phân, xét nghiệm máu hoặc nội soi sinh thiết.\nTrường hợp test hơi thở, bệnh nhân cần nhịn ăn ít nhất 6 tiếng trước khi test. Quy trình test diễn ra nhanh chóng, an toàn, không xâm lấn.\nKết quả sẽ được bác sĩ giải thích và tư vấn điều trị diệt HP nếu dương tính.', '', '2025-06-22 16:19:19', '2025-06-22 16:19:19', '[]', 'ieu-tri-viem-loet-da-day-ta-trang', 500000),
	(25, 'Tư vấn kiểm soát tăng huyết áp', 'Dịch vụ dành cho bệnh nhân có chỉ số huyết áp cao hoặc dao động bất thường, giúp kiểm soát và giảm nguy cơ đột quỵ, suy tim, bệnh mạch vành.', 'Bệnh nhân được đo huyết áp nhiều lần trong ngày, kết hợp khám lâm sàng và xét nghiệm liên quan như đường huyết, mỡ máu, creatinine, điện giải đồ.\nBác sĩ sẽ đánh giá toàn diện yếu tố nguy cơ, hướng dẫn điều chỉnh lối sống và kê đơn thuốc phù hợp nếu cần.\nMột số bệnh nhân sẽ được cấp sổ theo dõi huyết áp để ghi lại chỉ số và tái khám định kỳ theo hướng dẫn.', '', '2025-06-22 16:21:50', '2025-06-22 16:21:50', '[]', 'tu-van-kiem-soat-tang-huyet-ap', 500000),
	(26, 'Quản lý bệnh lý rối loạn chuyển hóa', 'Dịch vụ chẩn đoán và điều trị các rối loạn như tăng cholesterol, rối loạn đường huyết, hội chứng chuyển hóa – những yếu tố nguy cơ gây biến chứng tim mạch, tiểu đường.', 'Bác sĩ thực hiện khai thác tiền sử bệnh nhân, chỉ số BMI, vòng bụng.\nBệnh nhân được lấy máu kiểm tra đường huyết, HbA1c, lipid toàn phần, triglyceride, HDL, LDL…\nSau đó, bác sĩ đưa ra phác đồ điều trị bao gồm thuốc nếu cần, kèm theo hướng dẫn về chế độ ăn và tập luyện.\nBệnh nhân có thể được tư vấn kết hợp khám chuyên khoa dinh dưỡng nếu cần cải thiện lối sống tích cực.', '', '2025-06-22 16:22:16', '2025-06-22 16:22:16', '[]', 'quan-ly-benh-ly-roi-loan-chuyen-hoa', 500000),
	(27, 'Tư vấn và chẩn đoán rối loạn giấc ngủ', 'Dịch vụ giúp phát hiện sớm các rối loạn giấc ngủ như mất ngủ kinh niên, ngủ ngáy, ngưng thở khi ngủ, ngủ không sâu giấc... nhằm cải thiện chất lượng sống và hiệu suất làm việc.', 'Bệnh nhân sẽ được bác sĩ khai thác chi tiết tiền sử giấc ngủ, các biểu hiện bất thường trong khi ngủ, tình trạng lo âu – căng thẳng.\nCó thể thực hiện trắc nghiệm giấc ngủ (Sleep Questionnaire) và ghi nhật ký giấc ngủ.\nNếu nghi ngờ ngưng thở khi ngủ, bệnh nhân sẽ được giới thiệu thực hiện đo đa ký giấc ngủ (Polysomnography).\nSau đó, bác sĩ nội tổng quát tư vấn liệu trình điều trị bằng thuốc, hành vi – tâm lý hoặc chuyển tuyến chuyên sâu nếu cần.', '', '2025-06-22 16:23:27', '2025-06-22 16:23:27', '[]', 'tu-van-va-chan-oan-roi-loan-giac-ngu', 500000),
	(28, 'Kiểm tra chức năng gan', 'Dịch vụ đánh giá toàn diện chức năng gan, phát hiện sớm các vấn đề như viêm gan siêu vi, gan nhiễm mỡ, xơ gan, rối loạn men gan, đặc biệt ở người thường xuyên uống rượu bia, dùng thuốc kéo dài hoặc có bệnh lý mạn tính.', 'Bệnh nhân được bác sĩ khám tổng quát và khai thác thói quen ăn uống, sử dụng thuốc, rượu bia.\nTiến hành xét nghiệm men gan (ALT, AST, GGT), Bilirubin, Albumin, thời gian prothrombin.\nCó thể được chỉ định thêm siêu âm gan để đánh giá gan nhiễm mỡ, gan to, gan xơ hóa.\nSau khi có kết quả, bác sĩ sẽ tư vấn hướng điều trị, chế độ ăn kiêng, lối sống lành mạnh và theo dõi định kỳ.', '', '2025-06-22 16:23:54', '2025-06-22 16:23:54', '[]', 'kiem-tra-chuc-nang-gan', 500000),
	(29, 'Tầm soát bệnh mạch vành', 'Gói tầm soát nhằm phát hiện sớm nguy cơ thiếu máu cơ tim, nhồi máu cơ tim hoặc hẹp động mạch vành – nguyên nhân gây tử vong hàng đầu do bệnh tim mạch.', 'Bác sĩ tim mạch khai thác tiền sử cá nhân và gia đình, thăm khám triệu chứng như đau ngực, khó thở, hồi hộp…\nThực hiện điện tâm đồ lúc nghỉ và điện tâm đồ gắng sức nếu cần (ECG Stress Test).\nSiêu âm tim để đánh giá chức năng co bóp và lưu lượng máu nuôi tim.\nXét nghiệm máu bao gồm đường huyết, mỡ máu, men tim và chỉ số viêm CRP.\nTrong trường hợp nghi ngờ cao, bệnh nhân có thể được chỉ định chụp CT mạch vành.\nSau khi tổng hợp kết quả, bác sĩ đưa ra kết luận nguy cơ và tư vấn theo dõi, điều trị hoặc can thiệp nếu cần.', '', '2025-06-22 16:27:46', '2025-06-22 16:27:46', '[]', 'tam-soat-benh-mach-vanh', 500000),
	(30, 'Theo dõi và quản lý suy tim', 'Dịch vụ hỗ trợ người bệnh suy tim ổn định tình trạng, cải thiện triệu chứng khó thở, mệt mỏi, giảm tỷ lệ nhập viện tái phát và kéo dài tuổi thọ với chất lượng sống tốt hơn.', 'Bệnh nhân được bác sĩ chuyên khoa đánh giá mức độ suy tim theo phân độ NYHA, đo huyết áp, nhịp tim và triệu chứng lâm sàng.\nSiêu âm tim để kiểm tra phân suất tống máu (EF) và mức độ giãn buồng tim, hở van tim.\nXét nghiệm BNP hoặc NT-proBNP để đánh giá gánh nặng thất trái.\nKê toa điều trị suy tim theo phác đồ cá nhân hóa (ACEi, Beta-blocker, lợi tiểu, ARNI…).\nBệnh nhân sẽ được lập lịch tái khám định kỳ, hướng dẫn tự theo dõi cân nặng, tiểu tiện, triệu chứng tại nhà để phát hiện dấu hiệu suy tim mất bù sớm.', '', '2025-06-22 16:28:06', '2025-06-22 16:28:06', '[]', 'theo-doi-va-quan-ly-suy-tim', 500000),
	(31, 'Tư vấn và điều trị viêm đường hô hấp trên', 'Điều trị hiệu quả các bệnh lý viêm mũi, viêm họng, viêm amidan ở trẻ em – những bệnh phổ biến khi thay đổi thời tiết hoặc đi học trở lại, giúp bé nhanh khỏi bệnh và hạn chế tái phát.', 'Khám lâm sàng vùng tai – mũi – họng bởi bác sĩ Nhi, kiểm tra nhiệt độ, hạch cổ, nghe phổi.\nChỉ định xét nghiệm CRP, công thức máu hoặc test nhanh cúm nếu nghi nhiễm siêu vi.\nKê đơn thuốc phù hợp độ tuổi (kháng sinh, hạ sốt, xịt mũi...) và hướng dẫn chăm sóc tại nhà.\nBác sĩ tư vấn dấu hiệu cần tái khám và dự phòng tái nhiễm cho trẻ.', '', '2025-06-22 16:30:01', '2025-06-22 16:30:01', '[]', 'tu-van-va-ieu-tri-viem-uong-ho-hap-tren', 500000),
	(32, 'Khám và theo dõi hen suyễn ở trẻ em', 'Dịch vụ theo dõi dài hạn tình trạng hen suyễn ở trẻ, giúp kiểm soát cơn hen, hạn chế nhập viện và nâng cao chất lượng cuộc sống cho trẻ.', 'Bác sĩ đánh giá tiền sử dị ứng, hen gia đình, tần suất khò khè, ho đêm của trẻ.\nThực hiện đo chức năng hô hấp (nếu trẻ đủ tuổi hợp tác) hoặc test giãn phế quản.\nKê toa điều trị duy trì (ICS, LABA) và hướng dẫn kỹ năng sử dụng dụng cụ hít đúng cách.\nTheo dõi định kỳ, điều chỉnh phác đồ theo triệu chứng và mức độ kiểm soát cơn hen.', '', '2025-06-22 16:30:33', '2025-06-22 16:30:33', '[]', 'kham-va-theo-doi-hen-suyen-o-tre-em', 500000),
	(33, 'Tư vấn dinh dưỡng cho trẻ', 'Dịch vụ giúp đánh giá tình trạng dinh dưỡng của trẻ, phát hiện sớm tình trạng suy dinh dưỡng, thiếu vi chất hoặc béo phì; đồng thời xây dựng thực đơn khoa học, phù hợp từng độ tuổi và thể trạng.', 'Bác sĩ Nhi sẽ kiểm tra chiều cao, cân nặng, vòng đầu và vòng cánh tay của trẻ, tính toán chỉ số BMI và tra biểu đồ tăng trưởng WHO.\nKhai thác chế độ ăn hiện tại, thói quen ăn uống và các dấu hiệu bất thường như biếng ăn, tiêu hóa kém, táo bón...\nThực hiện xét nghiệm vi chất nếu nghi ngờ thiếu hụt (Vitamin D, sắt, kẽm, canxi...).\nXây dựng thực đơn mẫu, hướng dẫn cách chế biến phù hợp độ tuổi và lên lịch theo dõi dinh dưỡng định kỳ.', '', '2025-06-22 16:31:22', '2025-06-22 16:31:22', '[]', 'tu-van-dinh-duong-cho-tre', 500000),
	(34, 'Khám tiêu hóa nhi', 'Dịch vụ hỗ trợ chẩn đoán và điều trị các rối loạn tiêu hóa thường gặp ở trẻ em như nôn trớ, đầy bụng, tiêu chảy, táo bón, đau bụng kéo dài, dị ứng đạm sữa bò...', 'Bác sĩ Nhi khai thác chi tiết tiền sử bệnh lý tiêu hóa, các triệu chứng hiện tại và chế độ ăn uống của trẻ.\nThăm khám lâm sàng bụng, đo vòng bụng, đánh giá mất nước hoặc dấu hiệu nhiễm trùng.\nThực hiện xét nghiệm phân, men tiêu hóa (amylase, lipase), CRP, siêu âm bụng nếu cần.\nBác sĩ tư vấn chế độ ăn phù hợp và kê toa điều trị theo nguyên nhân (rối loạn tiêu hóa, nhiễm khuẩn, dị ứng...).', '', '2025-06-22 16:31:43', '2025-06-22 16:31:43', '[]', 'kham-tieu-hoa-nhi', 500000),
	(35, 'Tầm soát chậm phát triển ở trẻ em', 'Tầm soát sớm các rối loạn phát triển về vận động, ngôn ngữ, hành vi – cảm xúc giúp can thiệp kịp thời, cải thiện khả năng học tập và hòa nhập xã hội cho trẻ.', 'Bác sĩ Nhi sử dụng bộ câu hỏi sàng lọc theo độ tuổi (Denver II, M-CHAT, ASQ) để đánh giá vận động thô, tinh, giao tiếp, ngôn ngữ và hành vi xã hội.\nQuan sát trẻ chơi, phản xạ khi giao tiếp và tương tác với cha mẹ.\nNếu cần, chỉ định đo thính lực, khám mắt, xét nghiệm di truyền hoặc chuyển chuyên khoa Tâm lý – Can thiệp.\nBác sĩ hướng dẫn phụ huynh chương trình can thiệp tại nhà, theo dõi định kỳ từng giai đoạn phát triển.', '', '2025-06-22 16:32:04', '2025-06-22 16:32:04', '[]', 'tam-soat-cham-phat-trien-o-tre-em', 500000),
	(36, 'Khám và điều trị viêm tai giữa', 'Dịch vụ giúp chẩn đoán và điều trị viêm tai giữa cấp hoặc mạn tính – bệnh lý thường gặp ở trẻ em và người lớn khi thay đổi thời tiết, nhiễm siêu vi hoặc sau cảm cúm.', 'Bác sĩ khai thác triệu chứng như đau tai, sốt, chảy dịch tai, ù tai hoặc nghe kém.\nThăm khám bằng đèn soi tai và nội soi tai nếu cần để đánh giá tình trạng màng nhĩ, dịch trong tai giữa.\nChỉ định xét nghiệm CRP, công thức máu nếu nghi nhiễm trùng nặng.\nĐiều trị bằng kháng sinh, giảm viêm, nhỏ tai theo phác đồ. Trường hợp mạn tính có thể cần chích rạch màng nhĩ dẫn lưu hoặc đặt ống thông khí.', '', '2025-06-22 16:33:08', '2025-06-22 16:33:08', '[]', 'kham-va-ieu-tri-viem-tai-giua', 500000),
	(37, 'Rửa xoang – hút mũi chuyên sâu', 'Dịch vụ điều trị hỗ trợ viêm xoang, viêm mũi cấp và mạn tính bằng cách làm sạch dịch nhầy, mủ trong hốc mũi và các xoang, giảm nhanh triệu chứng nghẹt mũi, đau đầu, giảm khứu giác.', 'Bệnh nhân được bác sĩ khám mũi và đánh giá mức độ sung huyết, dịch nhầy.\nSử dụng thiết bị hút rửa áp lực thấp hoặc máy Proetz (nếu có) để hút sạch dịch từ các xoang mũi.\nDung dịch rửa chứa muối sinh lý hoặc kháng sinh – kháng viêm nhẹ tùy trường hợp.\nSau khi rửa, bác sĩ hướng dẫn vệ sinh mũi tại nhà và kê toa thuốc duy trì.', '', '2025-06-22 16:34:01', '2025-06-22 16:34:01', '[]', 'rua-xoang-hut-mui-chuyen-sau', 500000),
	(38, 'Soi da kỹ thuật số – Phát hiện sớm ung thư da', 'Sử dụng thiết bị soi da kỹ thuật số hiện đại giúp phát hiện sớm các tổn thương da nghi ngờ ác tính như nốt ruồi bất thường, dày sừng, ung thư biểu mô tế bào đáy…', 'Bác sĩ da liễu khám lâm sàng tổng quát toàn bộ vùng da, đặc biệt các vị trí có tiếp xúc ánh nắng.\nSử dụng máy soi da kỹ thuật số độ phân giải cao để phóng đại cấu trúc da (dermoscopy), lưu trữ hình ảnh so sánh theo thời gian.\nĐánh giá nguy cơ tổn thương ác tính và chỉ định sinh thiết nếu nghi ngờ.\nHướng dẫn theo dõi định kỳ hoặc tư vấn phẫu thuật – điều trị laser nếu cần.', '', '2025-06-22 16:35:37', '2025-06-22 16:35:37', '[]', 'soi-da-ky-thuat-so-phat-hien-som-ung-thu-da', 500000),
	(39, 'Điều trị viêm da cơ địa – viêm da dị ứng', 'Chẩn đoán và điều trị các thể viêm da dị ứng, đặc biệt ở trẻ nhỏ và người có cơ địa dị ứng, giúp giảm ngứa, khô da, ngăn ngừa bội nhiễm và cải thiện chất lượng sống.', 'Bác sĩ kiểm tra vùng tổn thương, khai thác tiền sử dị ứng (hen, viêm mũi dị ứng, tiền sử gia đình…).\nChỉ định các xét nghiệm dị ứng (IgE, test da nếu có), xét nghiệm loại trừ nhiễm trùng.\nKê toa thuốc chống viêm tại chỗ (corticoid, tacrolimus), dưỡng ẩm y khoa, thuốc kháng histamin.\nTư vấn chăm sóc da hàng ngày, chọn sữa tắm – kem dưỡng phù hợp và lịch tái khám định kỳ.', '', '2025-06-22 16:35:59', '2025-06-23 14:29:50', '[]', 'ieu-tri-viem-da-co-ia-viem-da-di-ung', 500000),
	(40, 'Tầm soát đái tháo đường', 'Dịch vụ giúp phát hiện sớm tình trạng tiền đái tháo đường hoặc đái tháo đường týp 2 ở người có yếu tố nguy cơ, nhằm điều chỉnh lối sống và điều trị kịp thời để phòng biến chứng.', 'Bác sĩ khai thác tiền sử gia đình, chỉ số BMI, vòng bụng, chế độ ăn uống và vận động.\nBệnh nhân được chỉ định xét nghiệm: đường huyết lúc đói, HbA1c, đường huyết sau ăn 2 giờ, insulin máu nếu cần.\nDựa vào kết quả, bác sĩ đánh giá nguy cơ, tư vấn chế độ dinh dưỡng, tập luyện, theo dõi định kỳ và kê đơn điều trị nếu chẩn đoán mắc đái tháo đường.\nMột số trường hợp được chuyển khám chuyên sâu hoặc hội chẩn đa chuyên khoa (tim mạch, mắt...).', '', '2025-06-22 16:37:07', '2025-06-22 16:37:07', '[]', 'tam-soat-ai-thao-uong', 500000),
	(41, 'Quản lý đái tháo đường týp 2', 'Dịch vụ theo dõi lâu dài cho bệnh nhân đã chẩn đoán đái tháo đường týp 2, giúp kiểm soát đường huyết ổn định, hạn chế biến chứng trên tim, thận, thần kinh, mắt và mạch máu.', 'Bác sĩ kiểm tra chỉ số đường huyết, cân nặng, huyết áp và các biến chứng đi kèm.\nXét nghiệm HbA1c định kỳ, chức năng thận (ure, creatinine), vi đạm niệu, mỡ máu và men gan.\nTư vấn điều chỉnh thuốc nếu cần: thuốc uống hoặc insulin, hướng dẫn tự theo dõi đường huyết tại nhà.\nHướng dẫn dinh dưỡng, tập luyện phù hợp, và thiết lập lịch tái khám định kỳ theo phác đồ cá nhân hóa.', '', '2025-06-22 16:37:25', '2025-06-22 16:37:25', '[]', 'quan-ly-ai-thao-uong-typ-2', 500000),
	(42, 'Tầm soát biến chứng đái tháo đường', 'Dịch vụ kiểm tra định kỳ nhằm phát hiện sớm các biến chứng nguy hiểm do đái tháo đường như tổn thương võng mạc, thận, thần kinh ngoại biên, tim mạch và bàn chân.', 'Bệnh nhân được khám chuyên khoa Nội tiết, đo huyết áp, BMI và đánh giá chỉ số mắt – chi dưới.\nXét nghiệm HbA1c, creatinine, vi đạm niệu, mỡ máu, men gan.\nThực hiện các chỉ định:\n\nSoi đáy mắt (võng mạc tiểu đường)\n\nĐiện tim (biến chứng tim mạch)\n\nKiểm tra cảm giác rung, nhiệt (tổn thương thần kinh ngoại biên)\n\nKhám bàn chân đái tháo đường (vết loét, giảm cảm giác...)\nBác sĩ đánh giá mức độ biến chứng và đưa ra hướng điều trị – dự phòng thích hợp.', '', '2025-06-22 16:37:52', '2025-06-22 16:37:52', '[]', 'tam-soat-bien-chung-ai-thao-uong', 500000),
	(43, 'Khám và tư vấn chăm sóc răng miệng', 'Dịch vụ giúp kiểm tra tổng quát tình trạng răng – lợi – miệng, phát hiện sớm các vấn đề như sâu răng, viêm nha chu, mảng bám, hôi miệng... và hướng dẫn chăm sóc răng miệng đúng cách.', 'Bác sĩ khám trực tiếp khoang miệng, sử dụng đèn chuyên dụng để kiểm tra các răng, lợi, mô mềm.\nGhi nhận vị trí sâu răng, viêm lợi, cao răng, tình trạng khớp cắn.\nNếu cần, chỉ định chụp phim X-quang răng kỹ thuật số (panorama hoặc từng răng).\nSau khi đánh giá, bác sĩ tư vấn cách vệ sinh răng đúng kỹ thuật, chế độ ăn phù hợp và lên kế hoạch điều trị các vấn đề phát hiện được.', '', '2025-06-22 16:38:49', '2025-06-22 16:38:49', '[]', 'kham-va-tu-van-cham-soc-rang-mieng', 500000),
	(44, ' Cạo vôi răng – đánh bóng răng', 'Dịch vụ loại bỏ mảng bám, vôi răng cứng tích tụ lâu ngày trên răng và dưới nướu giúp giảm viêm lợi, ngăn ngừa hôi miệng và duy trì nụ cười sáng khỏe.', 'Bệnh nhân được khám kiểm tra tình trạng viêm lợi và vôi răng.\nBác sĩ sử dụng máy siêu âm để cạo sạch vôi răng ở chân răng, kẽ răng và dưới nướu.\nTiếp theo là bước đánh bóng răng bằng đầu chổi mềm và dung dịch làm sạch bề mặt men răng.\nSau khi hoàn tất, bác sĩ hướng dẫn vệ sinh răng miệng và lịch cạo vôi định kỳ (mỗi 6 tháng/lần).', '', '2025-06-22 16:39:09', '2025-06-22 16:39:09', '[]', 'cao-voi-rang-anh-bong-rang', 500000),
	(45, 'Trám răng thẩm mỹ', 'Phục hồi các răng bị sâu hoặc vỡ nhỏ bằng vật liệu composite cao cấp cùng màu răng thật, giúp bảo tồn cấu trúc răng và cải thiện thẩm mỹ, chức năng ăn nhai.', 'Bác sĩ thăm khám xác định vị trí và mức độ sâu răng, vết vỡ răng.\nVệ sinh sạch lỗ sâu, tạo hình khoang trám, sau đó dùng vật liệu trám composite chuyên dụng tạo hình khít sát.\nChiếu đèn quang trùng hợp để đông cứng vật liệu, sau đó điều chỉnh khớp cắn và đánh bóng bề mặt.\nHướng dẫn chăm sóc và theo dõi độ bền của miếng trám.', '', '2025-06-22 16:39:28', '2025-06-22 16:39:28', '[]', 'tram-rang-tham-my', 500000),
	(46, 'Nhổ răng khôn không sang chấn', 'Dịch vụ giúp loại bỏ răng khôn mọc lệch, ngầm, gây viêm nhiễm hoặc đau nhức kéo dài mà không làm tổn thương các mô xung quanh, đảm bảo an toàn và hồi phục nhanh.', 'Bác sĩ thăm khám và chỉ định chụp X-quang răng khôn (Panorama, CT Cone Beam nếu cần) để xác định vị trí, hướng mọc và mức độ khó.\nGây tê tại chỗ bằng thuốc gây tê cục bộ, đảm bảo không đau.\nTiến hành nhổ răng bằng kỹ thuật tách mô nướu – bộc lộ thân răng – chia chân răng nếu cần, thao tác nhẹ nhàng hạn chế sang chấn.\nSau nhổ răng, bác sĩ khâu vết thương, kê toa giảm đau – kháng viêm – kháng sinh, dặn dò chăm sóc sau nhổ và lịch tái khám cắt chỉ.', '', '2025-06-22 16:40:10', '2025-06-22 16:40:10', '[]', 'nho-rang-khon-khong-sang-chan', 500000),
	(47, 'Tẩy trắng răng công nghệ LED', 'Dịch vụ làm trắng răng an toàn, hiệu quả cao bằng kỹ thuật chiếu đèn LED kết hợp gel tẩy trắng – giúp răng sáng bật tone ngay sau 1 liệu trình, không gây ê buốt kéo dài.', 'Bác sĩ kiểm tra tình trạng răng, loại trừ viêm lợi, sâu răng trước khi thực hiện.\nVệ sinh răng miệng sạch sẽ, cách ly môi – nướu bằng dụng cụ chuyên dụng.\nBôi gel tẩy trắng lên bề mặt răng, chiếu đèn LED lạnh trong 15–20 phút/lần (2–3 lần tùy tình trạng).\nSau liệu trình, bác sĩ tư vấn chế độ ăn uống và giữ màu răng trắng lâu dài.', '', '2025-06-22 16:40:33', '2025-06-22 16:40:33', '[]', 'tay-trang-rang-cong-nghe-led', 500000),
	(48, 'Tư vấn và niềng răng – chỉnh nha', 'Dịch vụ chỉnh nha giúp sắp xếp lại răng mọc lệch, hô, móm, chen chúc… để cải thiện thẩm mỹ và chức năng nhai – phát âm, áp dụng các kỹ thuật từ mắc cài kim loại đến Invisalign không mắc cài.', 'Bác sĩ khám tổng quát khớp cắn, hướng mọc răng và cung hàm.\nChụp X-quang răng – sọ nghiêng và lấy mẫu dấu răng.\nPhân tích phần mềm – xương hàm bằng phần mềm chỉnh nha chuyên dụng.\nĐưa ra phác đồ điều trị cá nhân hoá: thời gian, loại mắc cài, chi phí và lịch hẹn.\nSau khi bệnh nhân đồng ý, tiến hành gắn mắc cài và theo dõi định kỳ hàng tháng.', '', '2025-06-22 16:40:53', '2025-06-22 16:40:53', '[]', 'tu-van-va-nieng-rang-chinh-nha', 500000),
	(49, 'khám chân ', 'đá', 'đâsdsada', '/uploads/1750925529785-945142018.jpg', '2025-06-26 08:11:55', '2025-06-26 08:13:08', '[]', 'kham-chan', 500000),
