INSERT INTO `service_packages` (`id`, `name`, `slug`, `description`, `detail`, `benefits`, `precautions`, `image_url`, `price_json`, `createdAt`, `updatedAt`) VALUES
	(1, 'Gói Tầm Soát Ung Thư Tổng Quát', 'tam-soat-ung-thu', 'Gói khám toàn diện giúp phát hiện sớm các loại ung thư phổ biến, bảo vệ sức khỏe chủ động.', 'Bao gồm các xét nghiệm, chẩn đoán hình ảnh và nội soi nhằm phát hiện các dấu hiệu sớm của ung thư ở nhiều cơ quan khác nhau. Được thực hiện bởi đội ngũ bác sĩ chuyên môn cao với trang thiết bị hiện đại.', '✔️ Phát hiện sớm các loại ung thư phổ biến\n✔️ Tư vấn chuyên sâu với bác sĩ đầu ngành\n✔️ Rút ngắn thời gian chẩn đoán và điều trị', '✔️ Phát hiện sớm các loại ung thư phổ biến\r\n✔️ Tư vấn chuyên sâu với bác sĩ đầu ngành\r\n✔️ Rút ngắn thời gian chẩn đoán và điều trị', '/uploads/1749922759854-219957559.jpg', '{"f_60": 3500000, "m_60": 3300000, "f_u60": 3200000, "fm_60": 4200000, "m_u60": 3100000, "fm_u60": 3900000}', '2025-06-07 17:16:46', '2025-06-16 09:40:19'),
	(2, 'Gói Tầm Soát Tim Mạch', 'tam-soat-tim-mach', 'Đánh giá toàn diện chức năng tim và phát hiện sớm các bệnh lý tim mạch tiềm ẩn.', 'Bao gồm khám lâm sàng, đo điện tâm đồ, siêu âm tim, xét nghiệm mỡ máu, chức năng gan thận và chỉ số đường huyết.', 'Phát hiện sớm các bất thường tim mạch như tăng huyết áp, rối loạn nhịp, hẹp mạch vành, nhồi máu cơ tim.', 'Nhịn ăn ít nhất 8 giờ trước khi làm xét nghiệm máu. Không uống cà phê, bia rượu hoặc dùng thuốc tim trước ngày khám.', '/uploads/1749924707949-405502425.jpg', '{"f_60": 1300000, "m_60": 1280000, "f_u60": 1200000, "fm_60": 1350000, "m_u60": 1100000, "fm_u60": 1250000}', '2025-06-15 01:05:12', '2025-06-14 19:41:07'),
	(3, 'Gói Tầm Soát Ung Thư Nữ Giới', 'tam-soat-ung-thu-nu', 'Tầm soát sớm ung thư vú, cổ tử cung và buồng trứng bằng các phương pháp tiên tiến.', 'Khám phụ khoa, siêu âm đầu dò, xét nghiệm HPV DNA, Pap smear và chụp nhũ ảnh nếu cần.', 'Phát hiện sớm tế bào bất thường và ung thư ở giai đoạn đầu, nâng cao khả năng điều trị.', 'Khám vào ngày sạch kinh. Tránh quan hệ, đặt thuốc âm đạo trong vòng 48 giờ trước khám.', '/uploads/1749924696699-156889078.jpg', '{"f_60": 2400000, "f_u60": 2200000, "fm_60": 2500000, "fm_u60": 2300000}', '2025-06-15 01:05:36', '2025-06-14 18:11:36'),
	(4, 'Gói Khám & Điều Trị Da Liễu', 'goi-kham-da-lieu', 'Chăm sóc chuyên sâu cho làn da bị mụn, thâm, nám hoặc lỗ chân lông to.', 'Khám da chuyên khoa, soi da, điều trị bằng laser, lột da nhẹ và tư vấn dùng sản phẩm phù hợp.', 'Giúp làm sạch sâu, giảm mụn, đều màu da và cải thiện độ sáng tự nhiên.', 'Không trang điểm trước khi khám. Ngừng retinol, AHA, BHA 3 ngày trước liệu trình.', '/uploads/1749924677133-675606919.jpg', '{"f_60": "", "m_60": "", "f_u60": 900000, "fm_60": "", "m_u60": 850000, "fm_u60": 950000}', '2025-06-15 01:06:08', '2025-06-26 07:51:53'),
	(5, 'Gói Sức Khỏe Tổng Quát', 'suc-khoe-tong-quat', 'Gói khám định kỳ giúp đánh giá tổng thể sức khỏe của bạn.', 'Bao gồm khám nội tổng quát, công thức máu, nước tiểu, chức năng gan thận, đường huyết, mỡ máu.', 'Phát hiện sớm các bệnh mãn tính như tiểu đường, gan nhiễm mỡ, tăng huyết áp.', 'Nhịn ăn sáng, ngủ đủ giấc và không vận động mạnh trước khi khám.', '/uploads/1749924662301-490481389.jpg', '{"f_60": 1150000, "m_60": 1100000, "f_u60": 1050000, "m_u60": 1000000}', '2025-06-15 01:06:16', '2025-06-14 19:12:28'),
	(6, 'Gói Theo Dõi Tiểu Đường & Nội Tiết', 'goi-noi-tiet-tieu-duong', 'Gói dành riêng cho bệnh nhân đang hoặc có nguy cơ mắc tiểu đường.', 'Đo đường huyết lúc đói, HbA1c, insulin, xét nghiệm nước tiểu, khám nội và tư vấn dinh dưỡng.', 'Giúp chẩn đoán sớm tiền đái tháo đường, kiểm soát đường huyết hiệu quả, giảm biến chứng.', 'Nhịn ăn ít nhất 8 tiếng trước khi khám. Không uống trà, sữa, cà phê có đường.', '/uploads/1749924646114-291766634.jpg', '{"f_60": 1000000, "m_60": 500000, "f_u60": 1000000, "fm_60": 500000, "m_u60": 1000000, "fm_u60": 1500000}', '2025-06-15 01:06:21', '2025-06-26 08:17:42'),
	(10, 'Gói Tầm Soát Sức Khỏe Nữ Dưới 60 Tuổi', 'tam-soat-nu-duoi-60', ' Gói khám định kỳ toàn diện giúp phát hiện sớm các vấn đề tim mạch, tiểu đường, nội tiết và ung thư phụ khoa cho phụ nữ dưới 60.', 'Gồm khám tổng quát, siêu âm tim, đo điện tâm đồ, xét nghiệm máu, kiểm tra chức năng gan thận, khám phụ khoa và tầm soát ung thư cổ tử cung.', 'Phát hiện sớm bệnh lý, tăng hiệu quả điều trị, hỗ trợ tư vấn chuyên sâu theo độ tuổi.', 'Nhịn ăn 6-8 tiếng trước khi khám, tránh làm xét nghiệm phụ khoa khi đang trong kỳ kinh.', '/uploads/1750926944433-330778037.jpg', '{"f_60": 0, "m_60": 0, "f_u60": 4000000, "fm_60": 0, "m_u60": 0, "fm_u60": 4000000}', '2025-06-26 08:33:41', '2025-06-26 08:35:44'),
	(11, 'GÓI TẦM SOÁT UNG THƯ TỔNG QUÁT NAM & NỮ', 'tam-soat-ung-thu-tong-quat', 'Gói tầm soát chuyên sâu giúp phát hiện sớm nguy cơ các loại ung thư phổ biến ở cả nam và nữ, từ độ tuổi 40 trở lên.', 'Gói bao gồm nội soi tiêu hoá, khám phụ khoa hoặc tiết niệu theo giới tính, xét nghiệm máu tầm soát ung thư, siêu âm ổ bụng và tư vấn chuyên sâu. Các dịch vụ được cá nhân hóa theo giới và độ tuổi.', 'Phát hiện sớm các tổn thương tiền ung thư, tiết kiệm thời gian và chi phí điều trị về sau, thiết kế chuyên biệt theo giới.', 'Nhịn ăn 6–8 tiếng trước khi làm nội soi và xét nghiệm, không dùng thuốc nhuận tràng hoặc kháng sinh trước 3 ngày nếu không được chỉ định.', '/uploads/1750928804728-946267944.jpg', '{"f_60": 2200000, "m_60": 2000000, "f_u60": 2900000, "fm_60": 1800000, "m_u60": 2000000, "fm_u60": 2500000}', '2025-06-26 09:03:44', '2025-06-26 09:06:44');

